import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './SignupPage.css';

const SignupPage = () => {
  const [email, setEmail] = useState('');
  const [nom, setNom] = useState('');
  const [prenom, setPrenom] = useState('');
  const [password, setPassword] = useState('');
  const [password2, setPassword2] = useState('');
  const [showPwd, setShowPwd] = useState(false);
  const [showPwd2, setShowPwd2] = useState(false);
  const [msg, setMsg] = useState('');
  const [pwdError, setPwdError] = useState('');
  const [pwd2Error, setPwd2Error] = useState('');
  const navigate = useNavigate();

  // V√©rification de la complexit√© du mot de passe
  const validatePassword = (pwd) => {
    const regex = /^(?=.*[A-Z])(?=.*\d)(?=.*[!@#$%^&*()_+\-=\[\]{};':"\\|,.<>\/?]).{7,}$/;
    return regex.test(pwd);
  };

  const handleSubmit = async e => {
    e.preventDefault();
    setPwdError('');
    setPwd2Error('');
    setMsg('');

    if (!validatePassword(password)) {
      setPwdError("Le mot de passe doit contenir au moins 7 caract√®res, 1 majuscule, 1 chiffre et 1 caract√®re sp√©cial.");
      return;
    }
    if (password !== password2) {
      setPwd2Error("Les mots de passe ne correspondent pas.");
      return;
    }

    // TODO: Appeler l'API pour cr√©er le compte
    setMsg("Votre compte a bien √©t√© cr√©√© !");
    setTimeout(() => navigate('/login'), 1500);
  };

  return (
    <div className="signup-page container">
      <div className="signup-card">
        <h1>Cr√©er un compte</h1>
        <form onSubmit={handleSubmit}>
          <label>
            Pr√©nom
            <input
              type="text"
              placeholder="Votre pr√©nom"
              value={prenom}
              onChange={e => setPrenom(e.target.value)}
              required
            />
          </label>
          <label>
            Nom
            <input
              type="text"
              placeholder="Votre nom"
              value={nom}
              onChange={e => setNom(e.target.value)}
              required
            />
          </label>
          <label>
            Email
            <input
              type="email"
              placeholder="votre@email.fr"
              value={email}
              onChange={e => setEmail(e.target.value)}
              required
            />
          </label>
          <label>
            Mot de passe
            <div className="signup-password-field">
              <input
                type={showPwd ? "text" : "password"}
                placeholder="Votre mot de passe"
                value={password}
                onChange={e => setPassword(e.target.value)}
                required
                autoComplete="new-password"
              />
              <button
                type="button"
                className="signup-showpwd-btn"
                onClick={() => setShowPwd(v => !v)}
                tabIndex={-1}
                aria-label={showPwd ? "Masquer le mot de passe" : "Afficher le mot de passe"}
              >
                {showPwd ? "üôà" : "üëÅÔ∏è"}
              </button>
            </div>
            {pwdError && <div className="signup-error">{pwdError}</div>}
          </label>
          <label>
            Confirmer le mot de passe
            <div className="signup-password-field">
              <input
                type={showPwd2 ? "text" : "password"}
                placeholder="R√©p√©tez le mot de passe"
                value={password2}
                onChange={e => setPassword2(e.target.value)}
                required
                autoComplete="new-password"
              />
              <button
                type="button"
                className="signup-showpwd-btn"
                onClick={() => setShowPwd2(v => !v)}
                tabIndex={-1}
                aria-label={showPwd2 ? "Masquer le mot de passe" : "Afficher le mot de passe"}
              >
                {showPwd2 ? "üôà" : "üëÅÔ∏è"}
              </button>
            </div>
            {pwd2Error && <div className="signup-error">{pwd2Error}</div>}
          </label>
          <button className="btn btn-primary" type="submit">
            Cr√©er mon compte
          </button>
        </form>
        {msg && <div className="signup-msg">{msg}</div>}
        <div className="signup-help">
          <span>D√©j√† inscrit ? </span>
          <a href="/login">Se connecter</a>
        </div>
      </div>
    </div>
  );
};

export default SignupPage;
