import React from 'react';
import './ProductVariants.css';

function ProductVariants({ variants, selectedVariant, onVariantChange }) {
  if (!Array.isArray(variants) || variants.length === 0) {
    return <div>Aucune variante disponible</div>;
  }

  return (
    <div className="product-variants">
      <h4>Choisissez une variante :</h4>
      <div className="variant-list">
        {variants.map((variant, idx) => (
          <button
            key={variant.id}
            className={`variant-btn${selectedVariant === idx ? ' selected' : ''}`}
            onClick={() => onVariantChange(idx)}
          >
            {/* Affiche ici les infos utiles de la variante */}
            {variant.prix}â‚¬ {variant.taille && `- Taille ${variant.taille}`} {variant.couleur && `- ${variant.couleur}`}
          </button>
        ))}
      </div>
    </div>
  );
}

export default ProductVariants;
