import React, { useState, useEffect } from 'react';
import { useSearchParams, Link } from 'react-router-dom';
import './CategoryPage.css';

const CategoryPage = () => {
  const [searchParams] = useSearchParams();
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [sortBy, setSortBy] = useState('name');
  const [filterPrice, setFilterPrice] = useState('all');

  const category = searchParams.get('category') || 'Bagues';

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        setLoading(true);
        const response = await fetch('http://localhost:5105/api/products');
        
        if (!response.ok) {
          throw new Error('Erreur lors du chargement des produits');
        }
        
        const data = await response.json();
        setProducts(data);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchProducts();
  }, []);

  // Filtrage et tri des produits
  const filteredProducts = products
    .filter(product => {
      // Filtre par prix
      switch (filterPrice) {
        case 'under-1000': return product.prix < 1000;
        case '1000-2000': return product.prix >= 1000 && product.prix <= 2000;
        case 'over-2000': return product.prix > 2000;
        default: return true;
      }
    })
    .sort((a, b) => {
      switch (sortBy) {
        case 'price-asc': return a.prix - b.prix;
        case 'price-desc': return b.prix - a.prix;
        default: return a.nom.localeCompare(b.nom);
      }
    });

  if (loading) return <div className="loading">Chargement des produits...</div>;
  if (error) return <div className="error">Erreur : {error}</div>;

  return (
    <div className="category-page">
      <div className="container">
        <header className="category-header">
          <h1 className="category-title">{category}</h1>
          <p className="category-description">
            Découvrez notre collection de {category.toLowerCase()} d'exception
          </p>
        </header>

        <div className="category-controls">
          <div className="filters">
            <select 
              value={sortBy} 
              onChange={(e) => setSortBy(e.target.value)}
              className="filter-select"
            >
              <option value="name">Trier par nom</option>
              <option value="price-asc">Prix croissant</option>
              <option value="price-desc">Prix décroissant</option>
            </select>

            <select 
              value={filterPrice} 
              onChange={(e) => setFilterPrice(e.target.value)}
              className="filter-select"
            >
              <option value="all">Tous les prix</option>
              <option value="under-1000">Moins de 1000€</option>
              <option value="1000-2000">1000€ - 2000€</option>
              <option value="over-2000">Plus de 2000€</option>
            </select>
          </div>

          <div className="results-count">
            {filteredProducts.length} produits
          </div>
        </div>

        <div className="products-grid">
          {filteredProducts.map(product => (
            <div key={product.id} className="product-card">
              <Link to={`/product/${product.id}`} className="product-link">
                <div className="product-image">
                  <img 
                    src={product.variantes?.[0]?.imageUrl || 'https://images.unsplash.com/photo-1605100804763-247f67b3557e?w=400'} 
                    alt={product.nom} 
                  />
                  <div className="product-overlay">
                    <span className="view-product">Voir le produit</span>
                  </div>
                </div>
                <div className="product-info">
                  <h3 className="product-name">{product.nom}</h3>
                  <p className="product-description">{product.description}</p>
                  <p className="product-price">{product.prix}€</p>
                </div>
              </Link>
            </div>
          ))}
        </div>

        {filteredProducts.length === 0 && (
          <div className="no-products">
            <p>Aucun produit trouvé.</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default CategoryPage;
